public class Food extends Item {
	
	public Food(int layer,boolean visible) {
		super(layer, visible);
	}
	
	public boolean Eat(Player p) {
		if (p.GetNumOfAction() <= 0)
			return false;
		if (p.GetHP() == p.GetMaxHP()) {
			return false;
		}
		p.SetNumOfAction(p.GetNumOfAction() - 1);
		p.RemoveItem(this);
		return true;
	}
}
